import React, { useContext, useState } from 'react';
import {
    IonList, IonItem, IonLabel, IonInput, IonToggle, IonRadio, IonCheckbox, IonItemSliding,
    IonItemOption, IonItemOptions, IonContent, IonPage, IonAvatar, IonButton
} from '@ionic/react';
import { useHistory } from 'react-router';
import { AppContext } from "../initialState";
import "../pages/button.css";

const ChatItem = ({ user }) => {

    let history = useHistory();
    let { state, dispatch } = useContext(AppContext);
    function OpenChatPage() {
        alert("Opening Chat Window.");
        history.push("/chatpage");
        var action = {
            type: "ChatStarted",
            payload: user
        };
        dispatch(action);


    };

    return (<div>
        <IonItem  >

            <IonAvatar slot="start">
                <img src="./avatar-han.png"></img>
            </IonAvatar>
            <IonLabel>
                <h2><strong>{user[0]}</strong></h2>
                <h3>{user[1]}</h3>
                <p> Click Here To Talk to this fella!!</p>
                <IonButton onClick={(e) => { OpenChatPage() }}> </IonButton>
            </IonLabel>

        </IonItem>
    </div>
    );
}
export default ChatItem;
