import React, { useContext, useState } from 'react';
import {
    IonList, IonItem, IonLabel, IonInput, IonToggle, IonRadio, IonCheckbox, IonItemSliding,
    IonItemOption, IonItemOptions, IonContent, IonPage, IonAvatar, IonButton, useIonViewWillEnter
} from '@ionic/react';
import { Redirect, useHistory } from 'react-router';
import { AppContext } from "../initialState";
import "../pages/button.css";
import db from '../Firestore';

const ChatItem = ({ user }) => {

    let history = useHistory();
    let { state, dispatch } = useContext(AppContext);
    let [lastmsg, setLastmsg] = useState("Loading...");

    function OpenChatPage() {


        var action = {
            type: "ChatStarted",
            payload: user
        };
        dispatch(action);
        history.push({ pathname: '/chatpage' });


    };

    const fetchLastMsg = async () => {

        let channel1 = state.user.phone + "/" + user[1];
        //alert(channel1);
        let channel2 = user[1] + "/" + state.user.phone;
        //alert("Here");
        let k = await db.collection("messages").where("channel", "in", [channel1, channel2])
            .orderBy("dateTime", "desc").limit(1).onSnapshot((querySnapshot) => {
                let last = "";
                alert("cool");
                querySnapshot.forEach((doc) => {

                    last = doc.content;


                })
                setLastmsg(last);
                alert(last);
                last = "";
            })


    }
    useIonViewWillEnter(() => {
        fetchLastMsg();
    })



    return (<div>
        <IonItem  >

            <IonAvatar slot="start">
                <img src="./avatar-han.png"></img>
            </IonAvatar>
            <IonLabel>
                <h2><strong>{user[0]}</strong></h2>
                <h3>{user[1]}</h3>
                <p> {lastmsg}</p>
                <button onClick={(e) => { OpenChatPage() }} className="button-style"> Talk </button>
            </IonLabel>



        </IonItem>
    </div>
    );
}
export default ChatItem;
