import React, { useEffect, useState } from 'react';
import {
  IonList, IonItem, IonLabel, IonInput, IonToggle, IonRadio, IonCheckbox, IonItemSliding, IonHeader,
  IonItemOption, IonItemOptions, IonContent, IonPage
} from '@ionic/react';
import ChatItem from "../components/ChatItem";

import db from "../Firestore";






function Tab2() {
  let [dataFetched, check] = useState(false);
  let [AllUsers, setusers] = useState([]);

  var l = [];
  async function a() {

    let k = await db.collection("users").get().then((querySnapshot) => (
      querySnapshot.forEach((doc) => {
        var s = doc.data()
        var user = [];
        user.push(s['name']);
        user.push(s['phone']);
        l.push(user);
        //

      })
    ));
    setusers(l);
    check(true);


  };
  a();
  function Page() {
    return (
      <IonContent >
        <p>GrowBoy!! {AllUsers[0][0]}</p>
        {/*-- List of Text Items --*/}
        <IonList>
          {AllUsers.map((item) =>

            <ChatItem name={item[0]} phone={item[1]} key={item[1]} />
          )}

        </IonList>



        {/*-- List of Sliding Items --*/}

      </IonContent>
    )
  }
  return (<IonPage>

    {
      dataFetched ? <Page /> : <p>Loading...</p>
    }
  </IonPage>
  );
};
export default Tab2;
