import React from 'react';
import {
    IonList, IonItem, IonLabel, IonInput, IonToggle, IonRadio, IonCheckbox, IonItemSliding,
    IonItemOption, IonItemOptions, IonContent, IonPage
} from '@ionic/react';
import { useHistory } from 'react-router';

const ChatItem = ({ user }) => {



    return (
        <IonItem >

            <ion-avatar slot="start">
                <img src="./avatar-han.png"></img>
            </ion-avatar>
            <ion-label>
                <h2><strong>{user[0]}</strong></h2>
                <h3>{user[1]}</h3>
                <p>Click To Talk to this fella!!</p>
            </ion-label>
        </IonItem>
    );
}
export default ChatItem;
