import React, { useContext } from "react";
import { AppContext } from "../initialState";
import { useState } from "react";
import "./LoginPage.css";
import { IonContent, IonFooter, IonGrid, IonHeader, IonPage, IonRow, IonToolbar, IonCol, IonInput, IonButton, useIonViewDidEnter, useIonViewWillEnter } from "@ionic/react";
import db from "../Firestore";
import './styles.css';

const Chats = ({ messages }) => {
    return (messages.map((doc) => {
        return (<div key={doc['id']}>{doc.content}</div>)
    }));
}




const ChatPage = () => {
    let { state, dispatch } = useContext(AppContext);
    let [message, setMessage] = useState("");
    let [allmsgs, msgsRecieved] = useState(false);
    let [messages = [], setMessages] = useState();


    let channel = state.user['phone'] + "/" + state.chattingWith.phone;
    const sendMessage = async () => {
        let d = new Date();
        let utc = d.toUTCString();
        let now = Date.now();

        let message_body = {
            content: message,
            channel: channel,
            dateTime: now,
            sentBy: state.user['phone'],
            id: channel + utc
        };
        if (message) {
            let k = await db.collection("messages").add(message_body).then(() => {
                alert(state.chattingWith.phone);
            }).catch((error) => {
                alert(error);
            });
        }
        else {
            alert("Cannot Send Empty messages!");
        }
        setMessage("");

    }

    const fetch_message = async () => {





    }
    useIonViewWillEnter(async () => {
        let mess = [];
        let v = await db.collection("messages").where("channel", "in", [channel, channel.split("").reverse().join("")])
            .orderBy("dateTime").limit(100).onSnapshot((querySnapshot) => {

                querySnapshot.forEach((doc) => {
                    mess.push(doc.data());
                    //alert(mess.length);

                });
            });

        setMessages(mess);

        //alert(messages);
        msgsRecieved(true);
    })





    return (<IonPage>
        <IonHeader>{state.chattingWith.name}</IonHeader>
        <IonContent>

            {allmsgs ? <Chats messages={messages} /> : <p>Loading Messages...</p>}


        </IonContent>

        <IonFooter>
            <IonToolbar>
                <IonGrid>
                    <IonRow>
                        <IonCol size="11" >

                            <IonInput id="Messagebox" value={message} placeholder="Pour Your Heart Out.." onIonChange={(e) => { setMessage(e.target.value) }}></IonInput>
                        </IonCol>
                        <IonCol >

                            <IonButton size="2" onClick={(e) => { sendMessage() }}>Send</IonButton>
                        </IonCol>
                    </IonRow>
                </IonGrid>
            </IonToolbar>

        </IonFooter>

    </IonPage>
    );
}

export default ChatPage;
