import React, { useState } from 'react';
import {
  IonList, IonItem, IonLabel, IonInput, IonToggle, IonRadio, IonCheckbox, IonItemSliding, IonHeader,
  IonItemOption, IonItemOptions, IonContent, IonPage
} from '@ionic/react';
import ChatItem from "../components/ChatItem";

import db from "../Firestore";






function Tab2() {
  let [dataFetched, check] = useState(false);
  let [AllUsers, setusers] = useState([]);



  let a = async () => {
    var l = [];
    let k = await db.collection("users").get().then((querySnapshot) => (
      querySnapshot.forEach((doc) => {
        l.push(doc.data());
        setusers(l);
      })
    ));

    check(true);


  };
  //a();
  function Page() {
    return (
      <IonContent >
        <p>GrowBoy!! {AllUsers.length}</p>
        {/*-- List of Text Items --*/}
        <IonList>
          {AllUsers.map((item) => <ChatItem user={item} />)}

        </IonList>



        {/*-- List of Sliding Items --*/}

      </IonContent>
    )
  }
  return (<IonPage>
    <a></a>
    {
      dataFetched ? <Page /> : <p>Loading...</p>
    }
  </IonPage>
  );
};
export default Tab2;
