import React, { useContext, useState } from 'react';
import './LoginPage.css';
import { IonPage, IonHeader, IonContent, IonTitle, IonButton, IonLoading } from "@ionic/react"
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
} from "react-router-dom";
import Tab2 from './Tab2';
import db from "../Firestore";
import { loadPartialConfigAsync } from '@babel/core';
import signIn from './SignIn';
import { AppContext } from "../initialState"

function LoginForm() {
    let [showLoading, setShowLoading] = useState(false);
    let { state, dispatch } = useContext(AppContext);

    let [name, setName] = useState("");
    let [phoneNumber, setNumber] = useState("");
    let [password, setPassword] = useState("");

    const login = async () => {
        if (name == "" || password == "" || phoneNumber == "") {
            alert("Please Fill in all the details");
        }
        else {
            //Show Progresss
            setShowLoading(true);
            //Check the <inputsssss className=""></inputsssss>
            let user;
            alert(password);
            var ids;
            let user_exists = true;
            let user_id = await db.collection("users").doc("96").get().
                then((doc) => {

                    ids = doc.data()['user_id'];
                    if (!(phoneNumber.toString() in ids)) {
                        user_exists = false;
                    }

                });
            if (!user_exists) {
                alert("Account Doesnt Exist!, Press Sign In Button to make a new Account.");
                setShowLoading(false);
                return;
            }

            var fetchUser = db.collection("users").doc(phoneNumber).get()
                .then((doc) => {
                    if (password.toString() == doc.data()['password']) {
                        alert("Welcome" + name);
                        user = doc.data();
                        var action = {
                            type: "LoginUser"
                            , payload: user
                        };
                        dispatch(state, action);
                        setShowLoading(false);
                    }
                    else {
                        alert("Incorrect Password,Please Try Again.");
                        setShowLoading(false);
                    }
                });













        }

    };

    var login_form = (
        <IonPage>
            <IonHeader><IonTitle>letterBox  <h2>Log In</h2></IonTitle></IonHeader>
            <IonContent>
                <form>
                    <input className="input-style" placeholder="Name" type="text" onChange={(e) => { setName(e.target.value) }}
                    /><br></br>
                    <input className="input-style" placeholder="Phone" type="tel" maxLength="10" onChange={(e) => { setNumber(e.target.value) }} />
                    <input className="input-style" placeholder="Password" type="password" onChange={(e) => { setPassword(e.target.value) }} />
                </form>
                <IonButton onClick={(e) => { login() }}>Login</IonButton>
                <IonButton onClick={(e) => { signIn() }}>Sign In</IonButton>
                <IonLoading

                    isOpen={showLoading}
                    onDidDismiss={() => setShowLoading(false)}
                    message={'Please wait...'}

                />


            </IonContent>
        </IonPage>
    );
    return login_form;
};







export default LoginForm;
