import React, { useContext } from 'react';
import {
    IonList, IonItem, IonLabel, IonInput, IonToggle, IonRadio, IonCheckbox, IonItemSliding,
    IonItemOption, IonItemOptions, IonContent, IonPage, IonAvatar
} from '@ionic/react';
import { useHistory } from 'react-router';
import { AppContext } from "../initialState";

const ChatItem = ({ user }) => {
    let history = useHistory();
    let { state, dispatch } = useContext(AppContext);
    const OpenChatPage = () => {
        history.push("/chatpage");
        var action = {
            type: "ChatStarted",
            payload: user[0]
        };
        dispatch(action);

    };

    return (
        <IonItem onClick={(event) => { OpenChatPage() }} >

            <IonAvatar slot="start">
                <img src="./avatar-han.png"></img>
            </IonAvatar>
            <IonLabel>
                <h2><strong>{user[0]}</strong></h2>
                <h3>{user[1]}</h3>
                <p>Click To Talk to this fella!!</p>
            </IonLabel>
        </IonItem>
    );
}
export default ChatItem;
