import React, { useContext } from "react";
import { AppContext } from "../initialState";
import { useState } from "react";
import "./LoginPage.css";
import { IonContent, IonFooter, IonGrid, IonHeader, IonPage, IonRow, IonToolbar, IonCol, IonInput, IonButton } from "@ionic/react";
import db from "../Firestore";
import './styles.css';

const ChatPage = () => {
    let { state, dispatch } = useContext(AppContext);
    let [message, setMessage] = useState("");
    let channel = state.user['phone'] + "/" + state.chattingWith.phone;
    const sendMessage = async () => {

        let message_body = {
            content: message,
            channel: channel,
            dateTime: +Date.now(),
            sentBy: state.user['phone']
        };
        if (message) {
            let k = await db.collection("messages").add(message_body).then(() => {
                alert(state.chattingWith.phone);
            }).catch((error) => {
                alert(error);
            });
        }
        else {
            alert("Cannot Send Empty messages!");
        }
    }

    const fetchMessages = async () => {
        let SentMessages = await db.collection("messages").doc().get

    }

    return (
        <IonPage>
            <IonHeader>{state.chattingWith.name}</IonHeader>
            <IonContent>
                <p>This is the chatpage bro..</p>

                <IonFooter>
                    <IonToolbar>
                        <IonGrid>
                            <IonRow>
                                <IonCol size="11" >

                                    <IonInput placeholder="Pour Your Heart Out.." onChange={(e) => { setMessage(e.target.value) }}></IonInput>
                                </IonCol>
                                <IonCol >

                                    <IonButton size="2" onClick={(e) => { sendMessage() }}>Send</IonButton>
                                </IonCol>
                            </IonRow>
                        </IonGrid>
                    </IonToolbar>

                </IonFooter>
            </IonContent>
        </IonPage>
    );
}

export default ChatPage;
