import React, { useState } from 'react';
import './LoginPage.css';
import { IonPage, IonHeader, IonContent, IonTitle, IonButton, IonLoading } from "@ionic/react"
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
} from "react-router-dom";
import Tab2 from './Tab2';
import db from "../Firestore";
import { loadPartialConfigAsync } from '@babel/core';
import signIn from './SignIn';

function LoginForm() {
    let [showLoading, setShowLoading] = useState(false);

    let [name, setName] = useState("");
    let [phoneNumber, setNumber] = useState("");
    let [password, setPassword] = useState("");
    var new_user = (
        <h1></h1>
    );

    const login = async () => {
        if (name == "" || password == "" || phoneNumber == "") {
            alert("Please Fill in all the details");
        }
        else {
            //Show Progresss
            setShowLoading(true);
            //Check the <inputsssss className=""></inputsssss>




        }

    };
    function signIn(event) {
        window.open("./SignIn.jsx");
    }
    var login_form = (
        <IonPage>
            <IonHeader><IonTitle>letterBox  <h2>Log In</h2></IonTitle></IonHeader>
            <IonContent>
                <form>
                    <input className="input-style" placeholder="Name" type="text" onChange={(e) => { setName(e.target.value) }}
                    /><br></br>
                    <input className="input-style" placeholder="Phone" type="tel" maxLength="10" onChange={(e) => { setPassword(e.target.value) }} />
                    <input className="input-style" placeholder="Passcode" type="password" onChange={(e) => { setNumber(e.target.value) }} />
                </form>
                <IonButton onClick={(e) => { login() }}>Login</IonButton>
                <IonButton onClick={(e) => { signIn() }}>Sign In</IonButton>
                <IonLoading

                    isOpen={showLoading}
                    onDidDismiss={() => setShowLoading(false)}
                    message={'Please wait...'}

                />


            </IonContent>
        </IonPage>
    );
    return login_form;
};







export default LoginForm;
