import React, { useContext, useEffect, useState } from 'react';
import {
  IonList, IonItem, IonLabel, IonInput, IonToggle, IonRadio, IonCheckbox, IonItemSliding, IonHeader,
  IonItemOption, IonItemOptions, IonContent, IonPage
} from '@ionic/react';
import ChatItem from "./ChatItem";

import db from "../Firestore";
import { AppContext } from "../initialState";





function Tab2() {
  let [dataFetched, check] = useState(false);
  let [AllUsers, setusers] = useState([]);
  let { state, dispatch } = useContext(AppContext);

  var l = [];
  var text_display = "";
  async function a() {
    alert("Start Data Fetching");

    let k = await db.collection("users").get().then((querySnapshot) => (
      querySnapshot.forEach((doc) => {
        if (doc.data()['phone'] != state.user.phone) {
          var s = doc.data()
          var user = [];
          user.push(s['name']);
          user.push(s['phone']);
          l.push(user);
        }
        //

      })
    ));


    //


    setusers(l);
    alert("Finished Data Fetching");
    check(true);


  };
  a();
  function Page() {
    return (
      <IonContent >
        <p>Here you can see every user we have.</p>
        {/*-- List of Text Items --*/}
        <IonList>
          {AllUsers.map((item) =>

            <ChatItem user={item} key={item[1]} />
          )}

        </IonList>



        {/*-- List of Sliding Items --*/}

      </IonContent>
    )
  }
  return (<IonPage>

    {
      dataFetched ? <Page /> : <p>Loading...    {text_display}</p>
    }
  </IonPage>
  );
};
export default Tab2;
