import React from 'react';
import {
  IonList, IonItem, IonLabel, IonInput, IonToggle, IonRadio, IonCheckbox, IonItemSliding,
  IonItemOption, IonItemOptions, IonContent, IonPage
} from '@ionic/react';
import ChatItem from "../components/ChatItem";
import db from '../Firestore';

function Tab2() {
  var AllUser = [];
  const feedUsers = async () => {

    var a = await db.collection("users").get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        // doc.data() is never undefined for query doc snapshots
        AllUser.push(doc.data());
      });
    });
  };
  feedUsers();
  return (
    <IonPage>
      <IonContent >
        {/*-- List of Text Items --*/}
        <IonList>
          {AllUser.map((user) => { <ChatItem contact={user} /> })}
        </IonList>



        {/*-- List of Sliding Items --*/}

      </IonContent>
    </IonPage>)
};
export default Tab2;
