import React, { useContext } from "react";
import { AppContext } from "../initialState";
import "./LoginPage.css";
import { IonContent, IonFooter, IonGrid, IonHeader, IonPage, IonRow, IonToolbar, IonCol, IonInput, IonButton } from "@ionic/react";

const ChatPage = () => {
    let { state, dispatch } = useContext(AppContext);
    let [message, setmMessage] = useState("");

    return (
        <IonPage>
            <IonHeader>{state.chattingWith}</IonHeader>
            <IonContent>
                <p>This is thegh chadtpage bro..</p>
            </IonContent>
            <IonFooter>
                <IonToolbar>
                    <IonGrid>
                        <IonRow>
                            <IonCol size="11" >

                                <input className="input-style" placeholder="Pour your heart out.." onChange={(e) => { setmMessage(e.target.value) }}></input>
                            </IonCol>
                            <IonCol >

                                <IonButton size="2">Send</IonButton>
                            </IonCol>
                        </IonRow>
                    </IonGrid>
                </IonToolbar>

            </IonFooter>
        </IonPage>
    );
}

export default ChatPage;
